import Link from 'next/link'

const Layout = ({ children }) => {
    return (
        <div className="container">
            <nav>
                <Link>
                    <a href="">
                        <span className="main-title">
                            Hacker Next
                        </span>
                    </a>
                </Link>
            </nav>
            {children}
            <style jsx>
                {`
                    .container{
                        max-width: 800px;
                        margin: 0 auto;
                        background: #f6f6f6;
                    }
                    nav{
                        background: #f60;
                        padding: 1em;
                        height: 3rem;
                    }
                    nav > *{ /*For every immediate child of the nav*/
                        display: inline-block;
                        color: black;
                    }
                    nav a{
                        text-decoration: none;
                    }
                    nav .main-title{
                        font-weight: bold;
                    }
                `}
            </style>
            <style global jsx>
                {`
                    body{
                        background: white;
                        font-family: Verdana, Geneva, sans-serif;
                    }
                `}
            </style>
        </div>
    )
}
export default Layout;
