class Story extends React.Component {

    static async getInitialProps({ req, res, query }) {
        const storyId = query.id;
        const response = fetch(`https://node-hnapi.herokuapp.com/item/${story.id}`);
        const story = await response.json();// Parsing the response data into JSON and storing it in a var


    }

    render() {
        return (
            <div>
                <h1>Story Page</h1>
            </div>
        )
    }
}
export default Story;
