import React from 'react'
import fetch from 'isomorphic-fetch'
import Error from 'next/error'

class index extends React.Component {

    static async getInitialProps() {
        let stories;
        try {
            const response = await fetch('https://node-hnapi.herokuapp.com/news?page=1');
            stories = await response.json(); // Parsing the JSON Data
        }
        catch (err) {
            console.log(err);
            stories = []; // Not to throw any weird error on the page,
        }

        return {
            stories
        };
    }

    render() {
        const { stories } = this.props;

        if (stories.length === 0) {
            return <Error statusCode={503} />
        }

        return (
            <div>
                <h1>Hacker News Clone</h1>
                {
                    stories.map(story => { < h2 key={story.id}> {story.title}</h2> })
                }
            </div >
        )
    }
}
export default index;
